cd\(''c:/Users/Will/proximity_loss/Claude/PEEC_Script''\);
warning\(''off'', ''all''\);

% Suppress debug output during init
api = openmagnetics_api_interface\(\);

fprintf\(''\\n========== VERIFICATION ==========\\n''\);

% Core check
cores = api.get_cores\(\);
fprintf\(''\\nCores: %d total\\n'', length\(fieldnames\(cores\)\)\);
