    c = cores.ETD_39_20_13;
    fprintf\(''  ETD_39_20_13: shape=%s, Ae=%.1fmm2'', c.shape, c.Ae*1e6\);
    if isfield\(c, ''bobbin''\)
        fprintf\('', bobbin=%.2fx%.2fmm'', c.bobbin.width*1e3, c.bobbin.height*1e3\);
    end
    fprintf\(''\\n''\);
end

% Material check
mats = api.get_materials\(\);
fprintf\(''\\nMaterials: %d total\\n'', length\(fieldnames\(mats\)\)\);
mat_names = fieldnames\(mats\);
has_mfr = 0;
for i = 1:length\(mat_names\)
    if isfield\(mats.\(mat_names{i}\), ''manufacturer''\)
        has_mfr = has_mfr + 1;
    end
end
fprintf\(''  With manufacturer: %d\\n'', has_mfr\);

% Show some materials with manufacturers
count = 0;
for i = 1:length\(mat_names\)
    m = mats.\(mat_names{i}\);
    if isfield\(m, ''manufacturer''\) && count < 5
        fprintf\(''  %s -> %s\\n'', m.name, m.manufacturer\);
        count = count + 1;
    end
end

% Supplier check
suppliers = api.get_suppliers\(\);
fprintf\(''\\nSuppliers: %d\\n'', length\(suppliers\)\);
for i = 1:length\(suppliers\)
    s = suppliers{i};
    ms = api.get_materials_by_supplier\(s\);
